import React, { Component } from "react";
import Radar from "react-d3-radar";
import Plot from "react-plotly.js";
//import Chart from "chart.js";
import rcl from "react-chart-polar";

class DOA extends Component {
  state = {
    chart: {}
  };
  constructor(props) {
    this.state.chart = { msg: "test chart", osX: [], osY: [] };
  }

  getRandomData() {
    for (var i = 0, x = 0; i < 10; i++, x++) {
      this.state.chart.osX[i] = "id " + x + ": ";
      this.state.chart.osY[i] = Math.cos(x) + Math.sin(x);
    }
  }
  render() {
    return <rcl.ChartPolar data={this.state.chart} />;
  }
}

export default DOA;
